    }
];


const StyledWrapper = styled.div`
	padding: 1em 0 0 0;
`;

const StyledScrollTitle = styled.h2`
	font-size: .85em;
	text-transform: uppercase;
    padding-left: 1em;
`;

const StyledScrollView = styled.div`
	overflow-x: scroll;
	overflow-y: hidden;
	white-space: nowrap;
	/* padding: 0 0 1em 0; */
    padding: 1em;
	-webkit-overflow-scrolling: touch;
`;

const StyledCard = styled.div`
	display: inline-block;
    justify-content: center;
	height: 200px;
	width: 200px;
	background-color: #303030;
	margin: 0 1em 0 0;
    box-shadow: 0 0 15px -5px #111;
`;

const StyledCardTitle = styled.h3`
    margin: 0;
    padding: 0;
    font-size: .8em;
`;

const StyledCardDescription = styled.h3`
    margin: 0;
    padding: 0;
    font-size: 0.8em;
`;

const Card = ({ title, description, thumbnail_link }) => {
	return (
        <StyledCard>
            <img src={thumbnail_link} alt={title} />
            <StyledCardTitle>{ title }</StyledCardTitle>
            <StyledCardDescription>{ description }</StyledCardDescription>
        </StyledCard>
	)
}

const ScrollView = () => {
	return (
		<StyledWrapper>
			<StyledScrollTitle>
				Recently Added
			</StyledScrollTitle>
			<StyledScrollView>
                { testArr.map(card => (
                    <Card title={card.title} description={card.desciption} thumbnail_link={card.thumbnail_link} />
                ))}
			</StyledScrollView>		
		</StyledWrapper>
	)
}

export default ScrollView;
