import React from 'react';
import styled from 'styled-components';

const StyledWrapper = styled.div`
	padding: 1em 0 0 0;
`;

const StyledScrollTitle = styled.h2`
	font-size: .85em;
	text-transform: uppercase;
    padding-left: 1em;
`;

const StyledScrollView = styled.div`
	overflow-x: scroll;
	overflow-y: hidden;
	white-space: nowrap;
	/* padding: 0 0 1em 0; */
    padding: 1em;
	-webkit-overflow-scrolling: touch;
`;

const StyledCard = styled.div`
	display: inline-block;
	height: 200px;
	width: 200px;
	background-color: #303030;
	margin: 0 1em 0 0;
    box-shadow: 0 0 15px -5px #111;
`;

//const StyledCard

const Card = ({ title, description, thumbnail_link }) => {
	return (
        <StyledCard>
            <img src={thumbnail_link || "https://via.placeholder.com/150"} alt={title} />
        </StyledCard>
	)
}

const ScrollView = () => {
	return (
		<StyledWrapper>
			<StyledScrollTitle>
				Recently Added
			</StyledScrollTitle>
			<StyledScrollView>
				<StyledCard>one</StyledCard>
				<StyledCard>two</StyledCard>
				<StyledCard>three</StyledCard>
				<StyledCard>four</StyledCard>
				<StyledCard>five</StyledCard>
				<StyledCard>six</StyledCard>
			</StyledScrollView>		
		</StyledWrapper>
	)
}

export default ScrollView;
