    }

    return (
        <StyledList>
            <StyledListTitle>Videos</StyledListTitle>
            { listArr.map((item, i) => (
                <StyledListItem key={i} onClick={() => handleExpand(item)}>{ item.title }</StyledListItem>
            ))}
        </StyledList>
    );
}

export default ListView;
