    list-style-type: none;
    padding: 1em;
    margin: 1em 0 0 0;
`;

const StyledListTitleContainer = styled.div`
    width: 100%;
    display: flex;
    flex-direction: row;
`;

const StyledListTitle = styled.li`
	color: #ccc;
	font-size: 1.4em;
    font-weight: bold;
	margin-bottom: .7em;
    display: inline-block;
    margin-right: 1em;
`;

const StyledListItem = styled.li`
    border-bottom: 1px solid #464646;
    padding: 10px 12px 10px 12px;
    background: #222;
    cursor: pointer;

    :last-child {
        border-bottom: none;
