import React from 'react';
import styled from 'styled-components';
import { useSpring, animated } from 'react-spring';

const Modal = ({ children, toggle, action }) => {
    const fade = useSpring({
        opacity: toggle ? ('1') : ('0')
    })

    const growHeight = useSpring({
        height: toggle ? "200px" : "0px",
    });

    const StyledWrapper = styled(animated.div)`
        z-index: 8;
        width: 100vw;
        display: flex;
        justify-content: center;
        align-items: flex-end;
        flex-direction: column;
        position: absolute;
        bottom: 0;
        left: 0;
        opacity: 1;
        height: 100vh;
    `;

    const StyledShade = styled.div`
        width: 100%;
        flex: 1;
        background: rgba(0, 0, 0, .9);
    `;

    const StyledModalWrapper = styled(animated.div)`
        z-index: 9;
        width: 100vw;
        background-color: #1c1c1c;
        display: flex;
        justify-content: center;
        align-items: flex-start;
        padding: 40px 0 100px 0;
        border-top: 1px solid #000;
    `;



    return (
        <StyledWrapper style={fade}>
            <StyledShade onClick={action}/>
            <StyledModalWrapper style={growHeight}>{children}</StyledModalWrapper>
        </StyledWrapper>
    );
}

export default Modal;
